import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import BackButton from './BackButton';
import infuraStorage from '../services/MetaMaskStorage';

function StudentDashboard() {
  const [classes, setClasses] = useState([]);
  const [showJoinClass, setShowJoinClass] = useState(false);
  const [classCode, setClassCode] = useState('');
  const [selectedClass, setSelectedClass] = useState(null);
  const [activeTab, setActiveTab] = useState('recordings');
  const [error, setError] = useState('');
  const [isConnecting, setIsConnecting] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    document.body.classList.add('dashboard-page');
    const studentEmail = sessionStorage.getItem('userEmail');
    loadStudentData(studentEmail);

    return () => {
      document.body.classList.remove('dashboard-page');
    };
  }, []);

  const loadStudentData = async (email) => {
    try {
      setIsConnecting(true);
      const connectResult = await infuraStorage.connect();
      if (!connectResult.success) {
        setError('Failed to connect to storage');
        return;
      }

      // Load student's approved classes
      const allTeachers = await infuraStorage.getData('teachers') || [];
      let approvedClasses = [];

      for (const teacher of allTeachers) {
        const teacherClasses = await infuraStorage.getData(`classes_${teacher.email}`) || [];
        const studentClasses = teacherClasses.filter(cls => 
          cls.students.some(student => student.email === email)
        );
        approvedClasses = [...approvedClasses, ...studentClasses];
      }

      setClasses(approvedClasses);
    } catch (error) {
      console.error('Error loading student data:', error);
      setError('Failed to load student data');
    } finally {
      setIsConnecting(false);
    }
  };

  const joinClass = async () => {
    if (!classCode.trim()) {
      setError('Please enter a class code');
      return;
    }

    try {
      setError(''); // Clear any previous errors
      setIsConnecting(true);
      const studentEmail = sessionStorage.getItem('userEmail');
      const studentName = sessionStorage.getItem('userName');

      // Connect to storage
      const connectResult = await infuraStorage.connect();
      if (!connectResult.success) {
        setError('Failed to connect to storage');
        return;
      }

      // Find the class with matching code
      const teachers = await infuraStorage.getData('teachers') || [];
      let foundClass = null;
      let teacherEmail = null;

      const normalizedInputCode = classCode.trim().toUpperCase();
      console.log('Looking for class with code:', normalizedInputCode);

      for (const teacher of teachers) {
        console.log('Checking teacher:', teacher.email);
        const teacherClasses = await infuraStorage.getData(`classes_${teacher.email}`) || [];
        console.log('Teacher classes:', teacherClasses);

        const matchingClass = teacherClasses.find(cls => {
          console.log('Comparing:', cls.code, 'with:', normalizedInputCode);
          return cls.code && cls.code.trim().toUpperCase() === normalizedInputCode;
        });

        if (matchingClass) {
          foundClass = matchingClass;
          teacherEmail = teacher.email;
          break;
        }
      }

      if (!foundClass) {
        setError('Invalid class code. Please check the code and try again.');
        return;
      }

      // Check if already pending or approved
      const pendingStudents = await infuraStorage.getData('pending_students') || [];
      const isPending = pendingStudents.some(s => 
        s.email === studentEmail && s.classId === foundClass.id
      );

      const isApproved = foundClass.students && foundClass.students.some(s => s.email === studentEmail);

      if (isPending) {
        setError('You have already requested to join this class');
        return;
      }

      if (isApproved) {
        setError('You are already enrolled in this class');
        return;
      }

      // Add to pending students
      const newPendingStudent = {
        id: Date.now(),
        name: studentName,
        email: studentEmail,
        classId: foundClass.id,
        teacherEmail,
        className: foundClass.name,
        requestedAt: new Date().toISOString()
      };

      await infuraStorage.saveData('pending_students', [...pendingStudents, newPendingStudent]);
      setClassCode('');
      setShowJoinClass(false);
      alert(`Request sent to join ${foundClass.name}! Please wait for teacher approval.`);
    } catch (error) {
      console.error('Error joining class:', error);
      setError('Failed to join class. Please try again.');
    } finally {
      setIsConnecting(false);
    }
  };

  const renderContent = () => {
    if (!selectedClass) {
      return (
        <div className="classes-list">
          {classes.map(cls => (
            <div key={cls.id} className="class-item" onClick={() => setSelectedClass(cls)}>
              <h3>{cls.name}</h3>
              <div className="class-info">
                <span>Teacher: {cls.teacherEmail}</span>
              </div>
            </div>
          ))}

          {showJoinClass ? (
            <div className="join-class-form">
              <input
                type="text"
                value={classCode}
                onChange={(e) => setClassCode(e.target.value)}
                placeholder="Enter class code"
              />
              <div className="form-actions">
                <button onClick={joinClass}>Join Class</button>
                <button onClick={() => setShowJoinClass(false)} className="secondary-button">
                  Cancel
                </button>
              </div>
            </div>
          ) : (
            <button onClick={() => setShowJoinClass(true)} className="join-class-button">
              Join New Class
            </button>
          )}
        </div>
      );
    }

    return (
      <div className="class-dashboard">
        <div className="class-header">
          <button onClick={() => setSelectedClass(null)} className="back-to-classes">
            ‚Üê Back to Classes
          </button>
          <h2>{selectedClass.name}</h2>
        </div>

        <div className="tab-navigation">
          <button 
            className={`tab-button ${activeTab === 'recordings' ? 'active' : ''}`}
            onClick={() => setActiveTab('recordings')}
          >
            Recordings
          </button>
          <button 
            className={`tab-button ${activeTab === 'summaries' ? 'active' : ''}`}
            onClick={() => setActiveTab('summaries')}
          >
            Summaries
          </button>
        </div>

        {activeTab === 'recordings' && (
          <div className="recordings-list">
            {selectedClass.recordings.map(recording => (
              <div key={recording.id} className="recording-item">
                <div className="recording-info">
                  <span className="recording-date">
                    {new Date(recording.timestamp).toLocaleString()}
                  </span>
                  <span className="recording-duration">
                    Duration: {recording.duration}
                  </span>
                </div>
                <div className="recording-path">
                  File: {recording.localPath}
                </div>
              </div>
            ))}
          </div>
        )}

        {activeTab === 'summaries' && (
          <div className="summaries-list">
            {selectedClass.summaries.map(summary => (
              <div key={summary.id} className="summary-item">
                <div className="summary-date">{new Date(summary.timestamp).toLocaleString()}</div>
                <div className="summary-content">{summary.content}</div>
              </div>
            ))}
          </div>
        )}
      </div>
    );
  };

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <BackButton destination="/" />
        <h2>Student Dashboard</h2>
        <button 
          className="logout-button" 
          onClick={() => {
            sessionStorage.removeItem('userEmail');
            sessionStorage.removeItem('userRole');
            sessionStorage.removeItem('userName');
            navigate('/');
          }}
        >
          Logout
        </button>
      </div>

      <div className="storage-indicator">
        <div className="storage-dot infura-active"></div>
        <span>Using Infura Blockchain Storage</span>
      </div>

      {isConnecting && <div className="connecting-message">Connecting to Infura...</div>}
      {error && <div className="error-message">{error}</div>}

      <div className="dashboard-content">
        {renderContent()}
      </div>
    </div>
  );
}

export default StudentDashboard;
