import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import wasabiStorage from '../services/WasabiStorage';

function Login() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');

    try {
      // Admin login check
      if (username === 'admin' && password === 'duggy') {
        sessionStorage.setItem('userEmail', 'admin@example.com');
        sessionStorage.setItem('userRole', 'admin');
        navigate('/admin-dashboard');
        return;
      }

      // Connect to Wasabi
      const isConnected = await wasabiStorage.testConnection();
      if (!isConnected) {
        setError('Failed to connect to storage');
        return;
      }

      // Check user in Wasabi storage
      const userData = await wasabiStorage.getData(`users/${username}.json`);
      if (!userData) {
        setError('Invalid username/email or password');
        return;
      }

      if (userData.password !== password) {
        setError('Invalid username/email or password');
        return;
      }

      if (userData.role === 'teacher') {
        // Check teacher approval in teachers folder
        const teacherData = await wasabiStorage.getData(`teachers/${username}.json`);
        if (!teacherData || !teacherData.approved) {
          setError('Your teacher account is pending approval');
          return;
        }
      }

      // Store user info in session storage
      sessionStorage.setItem('userEmail', userData.email);
      sessionStorage.setItem('userRole', userData.role);

      // Navigate based on role
      switch (userData.role) {
        case 'teacher':
          navigate('/teacher-dashboard');
          break;
        case 'student':
          navigate('/student-dashboard');
          break;
        default:
          setError('Invalid user role');
      }
    } catch (error) {
      console.error('Login error:', error);
      setError('An error occurred during login');
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>

      <div className="storage-indicator">
        <div className="storage-dot wasabi-active"></div>
        <span>Using Wasabi Cloud Storage</span>
      </div>

      {error && <div className="error-message">{error}</div>}

      <form onSubmit={handleLogin}>
        <div className="form-group">
          <label htmlFor="username">Username or Email</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
            placeholder="Enter your username or email"
          />
        </div>

        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            placeholder="Enter your password"
          />
        </div>

        <button type="submit">Login</button>
      </form>

      <p>
        Don't have an account?{' '}
        <button 
          className="secondary-button"
          onClick={() => navigate('/create-account')}
        >
          Create Account
        </button>
      </p>
    </div>
  );
}

export default Login;
