import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import BackButton from './BackButton';
import infuraStorage from '../services/MetaMaskStorage';
import wasabiStorage from '../services/WasabiStorage';

function TeacherDashboard() {
  const [classes, setClasses] = useState([]);
  const [showCreateClass, setShowCreateClass] = useState(false);
  const [newClassName, setNewClassName] = useState('');
  const [selectedClass, setSelectedClass] = useState(null);
  const [pendingStudents, setPendingStudents] = useState([]);
  const [activeTab, setActiveTab] = useState('classes');
  const [recordings, setRecordings] = useState([]);
  const [teacherData, setTeacherData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [isConnecting, setIsConnecting] = useState(false);
  const [error, setError] = useState('');
  const [isRecording, setIsRecording] = useState(false);
  const [recordingTime, setRecordingTime] = useState(0);
  const mediaRecorderRef = useRef(null);
  const timeIntervalRef = useRef(null);
  const chunksRef = useRef([]);
  const navigate = useNavigate();

  // Memoize the connectAndLoadTeacherData function to prevent infinite loop
  const connectAndLoadTeacherData = React.useCallback(async (teacherEmail) => {
    try {
      setIsConnecting(true);
      setError('');

      // Connect to Infura
      const connectResult = await infuraStorage.connect();
      if (!connectResult.success) {
        setError(`Infura connection error: ${connectResult.error}`);
        setLoading(false);
        return;
      }

      await loadTeacherData(teacherEmail);
      await loadRecordings(teacherEmail);
      setLoading(false); // Set loading to false after all data is loaded
    } catch (error) {
      setError(`Error connecting to Infura: ${error.message}`);
      console.error('Connection error:', error);
      setLoading(false);
    } finally {
      setIsConnecting(false);
    }
  }, []);

  const loadRecordings = async (email) => {
    try {
      const savedRecordings = await infuraStorage.getData(`recordings_${email}`) || [];
      setRecordings(savedRecordings);
    } catch (error) {
      console.error('Error loading recordings:', error);
    }
  };

  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mediaRecorder = new MediaRecorder(stream);
      mediaRecorderRef.current = mediaRecorder;
      chunksRef.current = [];

      mediaRecorder.ondataavailable = (e) => {
        if (e.data.size > 0) {
          chunksRef.current.push(e.data);
        }
      };

      mediaRecorder.onstop = async () => {
        const blob = new Blob(chunksRef.current, { type: 'audio/webm' });
        const url = URL.createObjectURL(blob);
        const timestamp = new Date().toISOString();
        const newRecording = {
          id: Date.now(),
          url,
          timestamp,
          duration: recordingTime,
          localPath: `recording_${timestamp}.webm`
        };

        // Update recordings state
        const updatedRecordings = [...recordings, newRecording];
        setRecordings(updatedRecordings);

        // Update selected class recordings
        if (selectedClass) {
          const updatedClass = {
            ...selectedClass,
            recordings: [...selectedClass.recordings, newRecording]
          };
          setSelectedClass(updatedClass);

          // Update classes state
          const updatedClasses = classes.map(c => 
            c.id === selectedClass.id ? updatedClass : c
          );
          setClasses(updatedClasses);

          // Save to Infura
          const teacherEmail = sessionStorage.getItem('userEmail');
          await infuraStorage.saveData(`classes_${teacherEmail}`, updatedClasses);
        }

        // Save recording data
        await infuraStorage.saveData(`recordings_${sessionStorage.getItem('userEmail')}`, updatedRecordings);

        // Save to local storage
        const reader = new FileReader();
        reader.readAsDataURL(blob);
        reader.onloadend = () => {
          localStorage.setItem(`recording_${timestamp}`, reader.result);
        };

        stream.getTracks().forEach(track => track.stop());
      };

      mediaRecorder.start();
      setIsRecording(true);
      setRecordingTime(0);
      timeIntervalRef.current = setInterval(() => {
        setRecordingTime(prev => prev + 1);
      }, 1000);
    } catch (error) {
      console.error('Error starting recording:', error);
      setError('Failed to start recording. Please check your microphone permissions.');
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      clearInterval(timeIntervalRef.current);
      setIsRecording(false);
    }
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  useEffect(() => {
    document.body.classList.add('dashboard-page');
    const teacherEmail = sessionStorage.getItem('userEmail');
    connectAndLoadTeacherData(teacherEmail);

    return () => {
      document.body.classList.remove('dashboard-page');
      if (timeIntervalRef.current) {
        clearInterval(timeIntervalRef.current);
      }
      if (mediaRecorderRef.current && isRecording) {
        mediaRecorderRef.current.stop();
      }
    };
  }, [connectAndLoadTeacherData, isRecording]);

  const loadTeacherData = async (teacherEmail) => {
    try {
      setIsConnecting(true);
      const connectResult = await infuraStorage.connect();
      if (!connectResult.success) {
        setError('Failed to connect to storage');
        setLoading(false);
        return;
      }

      // Load or initialize teacher data
      const teachers = await infuraStorage.getData('teachers') || [];
      const existingTeacher = teachers.find(t => t.email === teacherEmail);

      if (!existingTeacher) {
        // Add teacher to teachers list if not exists
        await infuraStorage.saveData('teachers', [...teachers, { email: teacherEmail }]);
      }

      // Load teacher's classes
      const teacherClasses = await infuraStorage.getData(`classes_${teacherEmail}`) || [];
      setClasses(teacherClasses);

      // Load pending students for all classes
      const allPendingStudents = await infuraStorage.getData('pending_students') || [];
      const teacherPendingStudents = allPendingStudents.filter(s => s.teacherEmail === teacherEmail);
      setPendingStudents(teacherPendingStudents);

    } catch (error) {
      console.error('Error loading teacher data:', error);
      setError('Failed to load teacher data');
      setLoading(false);
    } finally {
      setIsConnecting(false);
    }
  };

  const generateClassCode = () => {
    // Generate a 6-character code with only uppercase letters and numbers
    const chars = '0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ';
    let code = '';
    for (let i = 0; i < 6; i++) {
      code += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    return code;
  };

  const createClass = async () => {
    if (!newClassName.trim()) {
      setError('Please enter a class name');
      return;
    }

    try {
      const teacherEmail = sessionStorage.getItem('userEmail');
      const classCode = generateClassCode();

      const newClass = {
        id: Date.now(),
        name: newClassName.trim(),
        code: classCode,
        teacherEmail,
        students: [],
        recordings: [],
        summaries: [],
        createdAt: new Date().toISOString()
      };

      // Ensure teacher exists in teachers list
      const teachers = await infuraStorage.getData('teachers') || [];
      if (!teachers.some(t => t.email === teacherEmail)) {
        await infuraStorage.saveData('teachers', [...teachers, { email: teacherEmail }]);
      }

      // Save the new class
      const updatedClasses = [...classes, newClass];
      await infuraStorage.saveData(`classes_${teacherEmail}`, updatedClasses);
      setClasses(updatedClasses);
      setNewClassName('');
      setShowCreateClass(false);

      // Show success message with class code
      alert(`Class created successfully! Class code: ${classCode}`);
    } catch (error) {
      console.error('Error creating class:', error);
      setError('Failed to create class');
    }
  };

  const approveStudent = async (studentId) => {
    try {
      const updatedPendingStudents = pendingStudents.filter(s => s.id !== studentId);
      const student = pendingStudents.find(s => s.id === studentId);

      if (student && selectedClass) {
        // Add student to class
        const updatedClasses = classes.map(c => {
          if (c.id === selectedClass.id) {
            return {
              ...c,
              students: [...c.students, student]
            };
          }
          return c;
        });

        await infuraStorage.saveData(`classes_${sessionStorage.getItem('userEmail')}`, updatedClasses);
        await infuraStorage.saveData('pending_students', updatedPendingStudents);

        setClasses(updatedClasses);
        setPendingStudents(updatedPendingStudents);
      }
    } catch (error) {
      console.error('Error approving student:', error);
      setError('Failed to approve student');
    }
  };

  const denyStudent = async (studentId) => {
    try {
      const updatedPendingStudents = pendingStudents.filter(s => s.id !== studentId);
      await infuraStorage.saveData('pending_students', updatedPendingStudents);
      setPendingStudents(updatedPendingStudents);
    } catch (error) {
      console.error('Error denying student:', error);
      setError('Failed to deny student');
    }
  };

  const removeStudent = async (studentId) => {
    try {
      if (selectedClass) {
        const updatedClasses = classes.map(c => {
          if (c.id === selectedClass.id) {
            return {
              ...c,
              students: c.students.filter(s => s.id !== studentId)
            };
          }
          return c;
        });

        await infuraStorage.saveData(`classes_${sessionStorage.getItem('userEmail')}`, updatedClasses);
        setClasses(updatedClasses);
      }
    } catch (error) {
      console.error('Error removing student:', error);
      setError('Failed to remove student');
    }
  };

  const handleDownload = async () => {
    try {
      // Get the teacher app executable as binary data
      const data = await wasabiStorage.getBinaryData('Teacher App.exe');
      if (!data) {
        throw new Error('Failed to download teacher app');
      }

      // Create a blob from the binary data
      const blob = new Blob([data], { type: 'application/x-msdownload' });
      const url = window.URL.createObjectURL(blob);

      // Create a temporary link and click it to start the download
      const link = document.createElement('a');
      link.href = url;
      link.download = 'Teacher App.exe';
      document.body.appendChild(link);
      link.click();

      // Clean up
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Error downloading teacher app:', error);
      alert('Failed to download teacher app. Please try again later.');
    }
  };

  const renderContent = () => {
    if (!selectedClass) {
      return (
        <div className="classes-list">
          {classes.map(cls => (
            <div key={cls.id} className="class-item" onClick={() => setSelectedClass(cls)}>
              <h3>{cls.name}</h3>
              <div className="class-info">
                <span>Class Code: {cls.code}</span>
                <span>{cls.students.length} Students</span>
              </div>
            </div>
          ))}

          {showCreateClass ? (
            <div className="create-class-form">
              <input
                type="text"
                value={newClassName}
                onChange={(e) => setNewClassName(e.target.value)}
                placeholder="Enter class name"
              />
              <div className="form-actions">
                <button onClick={createClass}>Create</button>
                <button onClick={() => setShowCreateClass(false)} className="secondary-button">
                  Cancel
                </button>
              </div>
            </div>
          ) : (
            <button onClick={() => setShowCreateClass(true)} className="create-class-button">
              Create New Class
            </button>
          )}
        </div>
      );
    }

    return (
      <div className="class-dashboard">
        <div className="class-header">
          <button onClick={() => setSelectedClass(null)} className="back-to-classes">
            ‚Üê Back to Classes
          </button>
          <h2>{selectedClass.name}</h2>
          <div className="class-code">Class Code: {selectedClass.code}</div>
        </div>

        <div className="tab-navigation">
          <button 
            className={`tab-button ${activeTab === 'recordings' ? 'active' : ''}`}
            onClick={() => setActiveTab('recordings')}
          >
            Recordings
          </button>
          <button 
            className={`tab-button ${activeTab === 'summaries' ? 'active' : ''}`}
            onClick={() => setActiveTab('summaries')}
          >
            Summaries
          </button>
          <button 
            className={`tab-button ${activeTab === 'students' ? 'active' : ''}`}
            onClick={() => setActiveTab('students')}
          >
            Manage Students
          </button>
        </div>

        {activeTab === 'recordings' && (
          <div className="recordings-section">
            <div className="recording-controls">
              <button 
                className={`record-button ${isRecording ? 'recording' : ''}`}
                onClick={isRecording ? stopRecording : startRecording}
              >
                {isRecording ? 'Stop Recording' : 'Record Lecture'}
              </button>
              {isRecording && (
                <div className="recording-timer">
                  Recording: {formatTime(recordingTime)}
                </div>
              )}
            </div>
            <div className="recordings-list">
              {selectedClass.recordings.map(recording => (
                <div key={recording.id} className="recording-item">
                  <div className="recording-info">
                    <span className="recording-date">
                      {new Date(recording.timestamp).toLocaleString()}
                    </span>
                    <span className="recording-duration">
                      Duration: {formatTime(recording.duration)}
                    </span>
                  </div>
                  <div className="recording-path">
                    File: {recording.localPath}
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {activeTab === 'summaries' && (
          <div className="summaries-list">
            {selectedClass.summaries.map(summary => (
              <div key={summary.id} className="summary-item">
                <div className="summary-date">{new Date(summary.timestamp).toLocaleString()}</div>
                <div className="summary-content">{summary.content}</div>
              </div>
            ))}
          </div>
        )}

        {activeTab === 'students' && (
          <div className="students-management">
            <div className="pending-students">
              <h3>Pending Approvals</h3>
              {pendingStudents.filter(s => s.classId === selectedClass.id).map(student => (
                <div key={student.id} className="pending-student-item">
                  <div className="student-info">
                    <div className="student-name">{student.name}</div>
                    <div className="student-email">{student.email}</div>
                  </div>
                  <div className="approval-buttons">
                    <button onClick={() => approveStudent(student.id)} className="approve-button">
                      Approve
                    </button>
                    <button onClick={() => denyStudent(student.id)} className="decline-button">
                      Deny
                    </button>
                  </div>
                </div>
              ))}
            </div>

            <div className="enrolled-students">
              <h3>Enrolled Students</h3>
              {selectedClass.students.map(student => (
                <div key={student.id} className="enrolled-student-item">
                  <div className="student-info">
                    <div className="student-name">{student.name}</div>
                    <div className="student-email">{student.email}</div>
                  </div>
                  <button onClick={() => removeStudent(student.id)} className="remove-button">
                    Remove
                  </button>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    );
  };

  if (loading) {
    return <div className="loading">Loading teacher dashboard...</div>;
  }

  // If the teacher account is pending approval
  if (teacherData && teacherData.pending) {
    return (
      <div className="dashboard-container">
        <div className="pending-approval">
          <h2>Account Pending Approval</h2>
          <p>Your teacher account is pending administrator approval.</p>
          <p>Please check back later or contact the administrator for more information.</p>
          <button onClick={() => navigate('/')} className="logout-button">Logout</button>

          <div className="storage-indicator">
            <div className="storage-dot infura-active"></div>
            <span>Using Infura Blockchain Storage</span>
          </div>

          {isConnecting && <div className="connecting-message">Connecting to Infura...</div>}
          {error && <div className="error-message">{error}</div>}
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <BackButton destination="/" />
        <h2>Teacher Dashboard</h2>
        <button 
          className="logout-button" 
          onClick={() => {
            sessionStorage.removeItem('userEmail');
            sessionStorage.removeItem('userRole');
            navigate('/');
          }}
        >
          Logout
        </button>
      </div>

      <div className="storage-indicator">
        <div className="storage-dot infura-active"></div>
        <span>Using Infura Blockchain Storage</span>
      </div>

      {isConnecting && <div className="connecting-message">Connecting to Infura...</div>}
      {error && <div className="error-message">{error}</div>}

      <div className="dashboard-content">
        <div className="download-section">
          <h3>Download Teacher App</h3>
          <p>Click the button below to download the Teacher App for your desktop.</p>
          <button className="download-button" onClick={handleDownload}>
            Download Teacher App
          </button>
        </div>
        {renderContent()}
      </div>
    </div>
  );
}

export default TeacherDashboard;
