import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import BackButton from './BackButton';
import wasabiStorage from '../services/WasabiStorage';

function AdminDashboard() {
  const [activeTab, setActiveTab] = useState('schools');
  const [schools, setSchools] = useState([]);
  const [teachers, setTeachers] = useState([]);
  const [selectedSchool, setSelectedSchool] = useState(null);
  const [newSchoolName, setNewSchoolName] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      // Connect to Wasabi
      const isConnected = await wasabiStorage.testConnection();
      if (!isConnected) {
        setError('Failed to connect to storage');
        return;
      }

      // Load schools
      const schoolsData = await wasabiStorage.getData('schools.json') || [];
      setSchools(schoolsData);

      // Load pending teachers from teacher-approval folder
      const teachersData = await wasabiStorage.listObjects('teacher-approval/');
      const pendingTeachers = [];

      for (const teacher of teachersData) {
        if (teacher.Key.endsWith('.json')) {
          const email = teacher.Key.replace('teacher-approval/', '').replace('.json', '');
          const teacherData = await wasabiStorage.getData(wasabiStorage.getPendingTeacherPath(email));
          if (teacherData) {
            pendingTeachers.push({
              ...teacherData,
              email: email
            });
          }
        }
      }

      console.log('Found pending teachers:', pendingTeachers);
      setTeachers(pendingTeachers);
    } catch (error) {
      console.error('Error loading data:', error);
      setError('Failed to load data');
    }
  };

  const handleCreateSchool = async () => {
    if (!newSchoolName.trim()) {
      setError('Please enter a school name');
      return;
    }

    try {
      const newSchool = {
        id: Date.now(),
        name: newSchoolName.trim(),
        createdAt: new Date().toISOString()
      };

      // Save to schools list
      const updatedSchools = [...schools, newSchool];
      await wasabiStorage.saveData('schools.json', updatedSchools);

      // Create school info file
      await wasabiStorage.saveData(wasabiStorage.getSchoolPath(newSchool.name), newSchool);

      setSchools(updatedSchools);
      setNewSchoolName('');
      alert('School created successfully!');
    } catch (error) {
      console.error('Error creating school:', error);
      setError('Failed to create school');
    }
  };

  const handleApproveTeacher = async (teacher) => {
    try {
      const teacherData = {
        ...teacher,
        approved: true,
        approvedAt: new Date().toISOString()
      };

      // Move teacher from teacher-approval to teachers folder
      const fromPath = wasabiStorage.getPendingTeacherPath(teacher.email);
      const toPath = wasabiStorage.getTeacherPath(teacher.email);

      await wasabiStorage.saveData(toPath, teacherData);
      await wasabiStorage.deleteObject(fromPath);

      setTeachers(teachers.filter(t => t.email !== teacher.email));
      alert('Teacher approved successfully!');
    } catch (error) {
      console.error('Error approving teacher:', error);
      setError('Failed to approve teacher');
    }
  };

  const handleDenyTeacher = async (teacher) => {
    try {
      // Simply delete from teacher-approval folder
      await wasabiStorage.deleteObject(wasabiStorage.getPendingTeacherPath(teacher.email));
      setTeachers(teachers.filter(t => t.email !== teacher.email));
      alert('Teacher denied successfully!');
    } catch (error) {
      console.error('Error denying teacher:', error);
      setError('Failed to deny teacher');
    }
  };

  return (
    <div className="admin-dashboard">
      <BackButton destination="/" />
      <h2>Admin Dashboard</h2>

      <div className="storage-indicator">
        <div className="storage-dot wasabi-active"></div>
        <span>Using Wasabi Cloud Storage</span>
      </div>

      {error && <div className="error-message">{error}</div>}

      <div className="tabs">
        <button 
          className={`tab ${activeTab === 'schools' ? 'active' : ''}`}
          onClick={() => setActiveTab('schools')}
        >
          Schools
        </button>
        <button 
          className={`tab ${activeTab === 'teachers' ? 'active' : ''}`}
          onClick={() => setActiveTab('teachers')}
        >
          Pending Teachers
        </button>
      </div>

      <div className="tab-content">
        {activeTab === 'schools' && (
          <div className="schools-section">
            <div className="create-school">
              <input
                type="text"
                value={newSchoolName}
                onChange={(e) => setNewSchoolName(e.target.value)}
                placeholder="Enter school name"
              />
              <button onClick={handleCreateSchool}>Create School</button>
            </div>

            <div className="schools-list">
              <h3>Schools</h3>
              {schools.map(school => (
                <div key={school.id} className="school-item">
                  <span>{school.name}</span>
                  <span className="school-date">
                    Created: {new Date(school.createdAt).toLocaleDateString()}
                  </span>
                </div>
              ))}
            </div>
          </div>
        )}

        {activeTab === 'teachers' && (
          <div className="teachers-section">
            <h3>Pending Teacher Approvals</h3>
            {teachers.length === 0 ? (
              <p>No pending teacher approvals</p>
            ) : (
              <div className="teachers-list">
                {teachers.map(teacher => (
                  <div key={teacher.email} className="teacher-item">
                    <div className="teacher-info">
                      <div className="teacher-name">{teacher.name}</div>
                      <div className="teacher-email">{teacher.email}</div>
                    </div>
                    <div className="teacher-actions">
                      <button 
                        className="approve-button"
                        onClick={() => handleApproveTeacher(teacher)}
                      >
                        Approve
                      </button>
                      <button 
                        className="deny-button"
                        onClick={() => handleDenyTeacher(teacher)}
                        style={{ backgroundColor: '#dc3545', color: 'white', marginLeft: '8px' }}
                      >
                        Deny
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default AdminDashboard;
