import React, { useState, useRef, useEffect } from 'react';
import './tutorBotStyle.css';
import LatexFormatter from './LatexFormatter';

function ChatBot() {
  const [messages, setMessages] = useState([]);
  const [inputText, setInputText] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [voiceEnabled, setVoiceEnabled] = useState(false);
  const chatBoxRef = useRef(null);

  // Scroll to bottom on new message
  useEffect(() => {
    if (chatBoxRef.current) {
      chatBoxRef.current.scrollTop = chatBoxRef.current.scrollHeight;
    }
  }, [messages]);

  // Render markdown, LaTeX, and bold
  const renderWithLatexAndMarkdown = (text) => {
    if (!text) return null;
    const lines = text.split(/\n|\r\n?/);
    return lines.map((line, idx) => {
      if (/^###\s+/.test(line)) {
        return <h3 key={idx} style={{marginTop: '1em'}}>{line.replace(/^###\s+/, '')}</h3>;
      } else if (/^##\s+/.test(line)) {
        return <h2 key={idx} style={{marginTop: '1em'}}>{line.replace(/^##\s+/, '')}</h2>;
      } else if (/^#\s+/.test(line)) {
        return <h1 key={idx} style={{marginTop: '1em'}}>{line.replace(/^#\s+/, '')}</h1>;
      } else if (line.trim() === '') {
        return <br key={idx} />;
      } else {
        // Bold (**...**)
        const boldRegex = /\*\*([^*]+)\*\*/g;
        const boldParts = [];
        let lastIndex = 0;
        let match;
        let key = 0;
        while ((match = boldRegex.exec(line)) !== null) {
          if (match.index > lastIndex) {
            boldParts.push(line.slice(lastIndex, match.index));
          }
          boldParts.push(<strong key={key++}>{match[1]}</strong>);
          lastIndex = match.index + match[0].length;
        }
        if (lastIndex < line.length) {
          boldParts.push(line.slice(lastIndex));
        }
        // LaTeX
        return (
          <span key={idx} style={{display: 'block', marginTop: '0.25em'}}>
            {boldParts.map((part, i) => {
              if (typeof part === 'string') {
                const regex = /(\$\$[\s\S]+?\$\$|\$[^$]+\$|\\\[[\s\S]+?\\\]|\\\([\s\S]+?\\\))/g;
                const latexParts = part.split(regex);
                return latexParts.map((sub, j) => {
                  if (sub.match(regex)) {
                    let latex = sub;
                    let displayMode = true;
                    if (latex.startsWith('$$') && latex.endsWith('$$')) {
                      latex = latex.slice(2, -2);
                      displayMode = true;
                    } else if (latex.startsWith('$') && latex.endsWith('$')) {
                      latex = latex.slice(1, -1);
                      displayMode = false;
                    } else if (latex.startsWith('\\[') && latex.endsWith('\\]')) {
                      latex = latex.slice(2, -2);
                      displayMode = true;
                    } else if (latex.startsWith('\\(') && latex.endsWith('\\)')) {
                      latex = latex.slice(2, -2);
                      displayMode = false;
                    }
                    return <LatexFormatter key={j} content={latex.trim()} displayMode={displayMode} />;
                  } else {
                    return <span key={j}>{sub}</span>;
                  }
                });
              } else {
                return part;
              }
            })}
          </span>
        );
      }
    });
  };

  const sendMessage = async () => {
    if (!inputText.trim()) return;
    setIsLoading(true);
    setMessages(prev => [...prev, { type: 'user', text: inputText }]);
    setInputText('');
    try {
      const response = await fetch('http://localhost:5000/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ message: inputText, history: messages.map(m => ({ role: m.type === 'user' ? 'user' : 'assistant', content: m.text })) })
      });
      const data = await response.json();
      setMessages(prev => [...prev, { type: 'bot', text: data.response }]);
    } catch (error) {
      setMessages(prev => [...prev, { type: 'error', text: 'Sorry, I encountered an error. Please try again.' }]);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div id="app-container" className="chat-active">
      <div id="main-content">
        <div className="greeting-area">
          <h1 className="greeting-text">How can Theta help you today?</h1>
        </div>
        <div id="input-container">
          <textarea
            id="user-input"
            placeholder="How can I help you today?"
            value={inputText}
            onChange={e => setInputText(e.target.value)}
            onKeyDown={e => { if (e.key === 'Enter' && !e.shiftKey) { e.preventDefault(); sendMessage(); } }}
            disabled={isLoading}
          />
          <div id="input-controls">
            <div className="left-controls"></div>
            <div className="right-controls">
              <button id="theta-voice-button" className="control-button" onClick={() => setVoiceEnabled(v => !v)} style={{ background: voiceEnabled ? '#007bff' : undefined, color: voiceEnabled ? '#fff' : undefined }}>Theta Voice</button>
              <button id="send-button" aria-label="Send message" onClick={sendMessage} disabled={isLoading}>â†‘</button>
            </div>
          </div>
        </div>
      </div>
      <div id="chat-box" ref={chatBoxRef} style={{ display: 'flex', flexDirection: 'column', overflowY: 'auto', maxHeight: '350px', minHeight: '200px', background: 'transparent', marginTop: '10px' }}>
        {messages.map((msg, idx) => (
          <div key={idx} className={msg.type === 'user' ? 'message user-message' : msg.type === 'bot' ? 'message bot-message' : 'message error-message'}>
            {renderWithLatexAndMarkdown(msg.text)}
          </div>
        ))}
        {isLoading && (
          <div className="message bot-message">Thinking...</div>
        )}
      </div>
    </div>
  );
}

export default ChatBot;
