import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import BackButton from './BackButton';
import database from '../services/Database';

function AdminDashboard() {
  const [activeTab, setActiveTab] = useState('schools');
  const [schools, setSchools] = useState([]);
  const [teachers, setTeachers] = useState([]);
  const [students, setStudents] = useState([]);
  const [selectedSchool, setSelectedSchool] = useState(null);
  const [newSchoolName, setNewSchoolName] = useState('');
  const [error, setError] = useState('');
  const [showCreateSchool, setShowCreateSchool] = useState(false);
  const [loading, setLoading] = useState(true);
  const [selectedTeacher, setSelectedTeacher] = useState(null);
  const [selectedClass, setSelectedClass] = useState(null);
  const [teacherClasses, setTeacherClasses] = useState([]);
  const [classStudents, setClassStudents] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setLoading(true);
      setError('');

      // Load teachers and students
      const [teachersData, studentsData] = await Promise.all([
        database.getTeachers(),
        database.getStudents()
      ]);

      setTeachers(teachersData);
      setStudents(studentsData);

      // Group teachers by school
      const schoolsMap = new Map();
      teachersData.forEach(teacher => {
        if (teacher.school_name) {
          if (!schoolsMap.has(teacher.school_name)) {
            schoolsMap.set(teacher.school_name, []);
          }
          schoolsMap.get(teacher.school_name).push(teacher);
        }
      });

      setSchools(Array.from(schoolsMap.entries()).map(([name, teachers]) => ({
        name,
        teachers
      })));

      setLoading(false);
    } catch (error) {
      console.error('Error loading data:', error);
      setError('Failed to load data');
      setLoading(false);
    }
  };

  const handleApproveTeacher = async (teacherId) => {
    try {
      await database.approveTeacher(teacherId);
      await loadData(); // Reload data to reflect changes
    } catch (error) {
      console.error('Error approving teacher:', error);
      setError('Failed to approve teacher');
    }
  };

  const handleRemoveTeacher = async (teacherId) => {
    if (!window.confirm('Are you sure you want to remove this teacher?')) {
      return;
    }
    try {
      await database.removeTeacher(teacherId);
      await loadData(); // Reload data to reflect changes
    } catch (error) {
      console.error('Error removing teacher:', error);
      setError('Failed to remove teacher');
    }
  };

  const handleRemoveStudent = async (studentId) => {
    if (!window.confirm('Are you sure you want to remove this student?')) {
      return;
    }
    try {
      await database.removeStudent(studentId);
      await loadData(); // Reload data to reflect changes
    } catch (error) {
      console.error('Error removing student:', error);
      setError('Failed to remove student');
    }
  };

  const handleTeacherClick = async (teacher) => {
    if (selectedTeacher?.id === teacher.id) {
      setSelectedTeacher(null);
      setTeacherClasses([]);
      setSelectedClass(null);
      setClassStudents([]);
    } else {
      setSelectedTeacher(teacher);
      setSelectedClass(null);
      setClassStudents([]);
      try {
        const classes = await database.getTeacherClasses(teacher.id);
        setTeacherClasses(classes);
      } catch (error) {
        console.error('Error loading teacher classes:', error);
        setError('Failed to load teacher classes');
      }
    }
  };

  const handleClassClick = async (classData) => {
    if (selectedClass?.id === classData.id) {
      setSelectedClass(null);
      setClassStudents([]);
    } else {
      setSelectedClass(classData);
      try {
        const students = await database.getStudentClasses(classData.id);
        setClassStudents(students);
      } catch (error) {
        console.error('Error loading class students:', error);
        setError('Failed to load class students');
      }
    }
  };

  if (loading) {
    return <div className="loading">Loading admin dashboard...</div>;
  }

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <BackButton destination="/" />
        <h2>Admin Dashboard</h2>
        <button 
          className="logout-button" 
          onClick={() => {
            sessionStorage.removeItem('userEmail');
            sessionStorage.removeItem('userRole');
            navigate('/');
          }}
        >
          Logout
        </button>
      </div>

      {error && <div className="error-message">{error}</div>}

      <div className="tabs">
        <button 
          className={`tab ${activeTab === 'schools' ? 'active' : ''}`}
          onClick={() => setActiveTab('schools')}
        >
          Schools
        </button>
        <button 
          className={`tab ${activeTab === 'teachers' ? 'active' : ''}`}
          onClick={() => setActiveTab('teachers')}
        >
          Teachers
        </button>
        <button 
          className={`tab ${activeTab === 'students' ? 'active' : ''}`}
          onClick={() => setActiveTab('students')}
        >
          Students
        </button>
      </div>

      <div className="content">
        {activeTab === 'schools' && (
          <div className="schools-section">
            <h3>Schools</h3>
            <button onClick={() => setShowCreateSchool(true)}>Add School</button>
            {schools.map(school => (
              <div key={school.name} className="school-card">
                <h4>{school.name}</h4>
                <p>Teachers: {school.teachers.length}</p>
              </div>
            ))}
          </div>
        )}

        {activeTab === 'teachers' && (
          <div className="teachers-section">
            <h3>Teachers</h3>
            <div className="teachers-list">
              {teachers.map(teacher => (
                <div key={teacher.id} className="teacher-card">
                  <div className="teacher-info">
                    <h4>{teacher.name}</h4>
                    <p>Email: {teacher.email}</p>
                    <p>School: {teacher.school_name}</p>
                    <p>Status: {teacher.approved ? 'Approved' : 'Pending'}</p>
                  </div>
                  <div className="teacher-actions">
                    {!teacher.approved && (
                      <button onClick={() => handleApproveTeacher(teacher.id)}>
                        Approve
                      </button>
                    )}
                    <button 
                      className="remove-button"
                      onClick={() => handleRemoveTeacher(teacher.id)}
                    >
                      Remove
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        {activeTab === 'students' && (
          <div className="students-section">
            <h3>Students</h3>
            <div className="students-list">
              {students.map(student => (
                <div key={student.id} className="student-card">
                  <div className="student-info">
                    <h4>{student.name}</h4>
                    <p>Email: {student.email}</p>
                    <p>School: {student.school_name}</p>
                  </div>
                  <div className="student-actions">
                    <button 
                      className="remove-button"
                      onClick={() => handleRemoveStudent(student.id)}
                    >
                      Remove
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default AdminDashboard;
