import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import BackButton from './BackButton';
import wasabiStorage from '../services/WasabiStorage';

function CreateAccount() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [schools, setSchools] = useState([]);
  const [selectedSchool, setSelectedSchool] = useState('');
  const [role, setRole] = useState('student');
  const navigate = useNavigate();

  useEffect(() => {
    loadSchools();
  }, []);

  const loadSchools = async () => {
    try {
      const schoolsData = await wasabiStorage.getData('schools.json') || [];
      setSchools(schoolsData);
      if (schoolsData.length > 0) {
        setSelectedSchool(schoolsData[0].name);
      }
    } catch (error) {
      console.error('Error loading schools:', error);
      setError('Failed to load schools');
    }
  };

  const handleCreateAccount = async (e) => {
    e.preventDefault();
    setError('');

    try {
      // Connect to Wasabi and ensure folders exist
      const isConnected = await wasabiStorage.testConnection();
      if (!isConnected) {
        setError('Failed to connect to storage');
        return;
      }

      // Initialize folders if they don't exist
      await wasabiStorage.initializeFolders();

      // Check if user already exists in pending or approved locations
      let existingUser = null;
      if (role === 'teacher') {
        // Check pending teachers
        existingUser = await wasabiStorage.getData(`teacher-approval/${email}.json`);
        if (existingUser) {
          setError('An account with this email is already pending approval');
          return;
        }
        // Check approved teachers
        const schools = await wasabiStorage.getData('schools.json') || [];
        for (const school of schools) {
          existingUser = await wasabiStorage.getData(`${school.name}/teachers/${email}/info.json`);
          if (existingUser) {
            setError('An account with this email already exists');
            return;
          }
        }
      } else {
        // Check pending students
        existingUser = await wasabiStorage.getData(`student-approval/${email}.json`);
        if (existingUser) {
          setError('An account with this email is already pending approval');
          return;
        }
        // Check approved students
        const schools = await wasabiStorage.getData('schools.json') || [];
        for (const school of schools) {
          existingUser = await wasabiStorage.getData(`${school.name}/students/${email}/info.json`);
          if (existingUser) {
            setError('An account with this email already exists');
            return;
          }
        }
      }

      // Create user data
      const userData = {
        email,
        password,
        name,
        role,
        school: selectedSchool,
        createdAt: new Date().toISOString()
      };

      // Save user data
      if (role === 'teacher') {
        // Save teacher as pending
        await wasabiStorage.saveData(`teacher-approval/${email}.json`, userData);
        setError('Your teacher account has been created and is pending admin approval');
      } else {
        // Save student as pending
        await wasabiStorage.saveData(`student-approval/${email}.json`, userData);
        setError('Your student account has been created and is pending approval');
      }
    } catch (error) {
      console.error('Create account error:', error);
      setError('An error occurred while creating your account');
    }
  };

  return (
    <div className="create-account-container">
      <BackButton destination="/" />
      <h2>Create Account</h2>

      <div className="storage-indicator">
        <div className="storage-dot wasabi-active"></div>
        <span>Using Wasabi Cloud Storage</span>
      </div>

      {error && <div className="error-message">{error}</div>}

      <form onSubmit={handleCreateAccount}>
        <div className="form-group">
          <label htmlFor="name">Name</label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
            placeholder="Enter your name"
            style={{ color: '#000' }}
          />
        </div>

        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            placeholder="Enter your email"
            style={{ color: '#000' }}
          />
        </div>

        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            placeholder="Enter your password"
            style={{ color: '#000' }}
          />
        </div>

        <div className="form-group">
          <label htmlFor="role">Role</label>
          <select
            id="role"
            value={role}
            onChange={(e) => setRole(e.target.value)}
            required
            style={{ color: '#000' }}
          >
            <option value="student">Student</option>
            <option value="teacher">Teacher</option>
          </select>
        </div>

        {role === 'teacher' && (
          <div className="form-group">
            <label htmlFor="school">Select School</label>
            <select
              id="school"
              value={selectedSchool}
              onChange={(e) => setSelectedSchool(e.target.value)}
              required
              style={{ color: '#000' }}
            >
              {schools.map(school => (
                <option key={school.id} value={school.name}>
                  {school.name}
                </option>
              ))}
            </select>
          </div>
        )}

        <button type="submit">Create Account</button>
      </form>

      <p>
        Already have an account?{' '}
        <button 
          className="secondary-button"
          onClick={() => navigate('/')}
        >
          Login
        </button>
      </p>
    </div>
  );
}

export default CreateAccount;
