import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import wasabiStorage from '../services/WasabiStorage';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [schools, setSchools] = useState([]);
  const [selectedSchool, setSelectedSchool] = useState('');
  const [role, setRole] = useState('student');
  const navigate = useNavigate();

  useEffect(() => {
    loadSchools();
  }, []);

  const loadSchools = async () => {
    try {
      const schoolsData = await wasabiStorage.getData('schools.json') || [];
      setSchools(schoolsData);
      if (schoolsData.length > 0) {
        setSelectedSchool(schoolsData[0].name);
      }
    } catch (error) {
      console.error('Error loading schools:', error);
      setError('Failed to load schools');
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');

    try {
      // Admin login check
      if ((email === 'admin' || email === 'admin@example.com') && password === 'duggy') {
        sessionStorage.setItem('userEmail', 'admin');
        sessionStorage.setItem('userRole', 'admin');
        navigate('/admin-dashboard');
        return;
      }

      // Connect to Wasabi
      const isConnected = await wasabiStorage.testConnection();
      if (!isConnected) {
        setError('Failed to connect to storage');
        return;
      }

      // For teachers, check if they selected a school
      if (role === 'teacher' && !selectedSchool) {
        setError('Please select a school');
        return;
      }

      // Check all possible locations for the user
      let userData = null;
      let isPending = false;

      if (role === 'teacher') {
        const pendingTeacher = await wasabiStorage.getData(`teacher-approval/${email}.json`);
        const approvedTeacher = await wasabiStorage.getData(`${selectedSchool}/teachers/${email}/info.json`);
        if (approvedTeacher) {
          userData = approvedTeacher;
        } else if (pendingTeacher) {
          userData = pendingTeacher;
          isPending = true;
        }
      } else {
        const pendingStudent = await wasabiStorage.getData(`student-approval/${email}.json`);
        const approvedStudent = await wasabiStorage.getData(`${selectedSchool}/students/${email}/info.json`);
        if (approvedStudent) {
          userData = approvedStudent;
        } else if (pendingStudent) {
          userData = pendingStudent;
          isPending = true;
        }
      }

      if (!userData) {
        setError('Invalid email or password');
        return;
      }

      if (userData.password !== password) {
        setError('Invalid email or password');
        return;
      }

      // Only check pending status for teachers
      if (isPending && role === 'teacher') {
        setError('Your teacher account is pending admin approval');
        return;
      }

      // Store user info in session storage
      sessionStorage.setItem('userEmail', userData.email);
      sessionStorage.setItem('userRole', role);
      sessionStorage.setItem('userName', userData.name);
      sessionStorage.setItem('userSchool', selectedSchool);

      // Navigate based on role
      if (role === 'teacher') {
        navigate('/teacher-dashboard');
      } else {
        navigate('/student-dashboard');
      }
    } catch (error) {
      console.error('Login error:', error);
      setError('An error occurred during login');
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>

      <div className="storage-indicator">
        <div className="storage-dot wasabi-active"></div>
        <span>Using Wasabi Cloud Storage</span>
      </div>

      {error && <div className="error-message">{error}</div>}

      <form onSubmit={handleLogin}>
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="text"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            placeholder="Enter your email"
            style={{ color: '#000' }}
          />
        </div>

        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            placeholder="Enter your password"
            style={{ color: '#000' }}
          />
        </div>

        <div className="form-group">
          <label htmlFor="role">Role</label>
          <select
            id="role"
            value={role}
            onChange={(e) => setRole(e.target.value)}
            required
            style={{ color: '#000' }}
          >
            <option value="student">Student</option>
            <option value="teacher">Teacher</option>
          </select>
        </div>

        {role === 'teacher' && (
          <div className="form-group">
            <label htmlFor="school">Select School</label>
            <select
              id="school"
              value={selectedSchool}
              onChange={(e) => setSelectedSchool(e.target.value)}
              required
              style={{ color: '#000' }}
            >
              {schools.map(school => (
                <option key={school.id} value={school.name}>
                  {school.name}
                </option>
              ))}
            </select>
          </div>
        )}

        <button type="submit">Login</button>
      </form>

      <p>
        Don't have an account?{' '}
        <button 
          className="secondary-button"
          onClick={() => navigate('/create-account')}
        >
          Create Account
        </button>
      </p>
    </div>
  );
}

export default Login;
