import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import database from '../services/Database.js';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [role, setRole] = useState('student');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');

    try {
      // Admin login check
      if ((email === 'admin' || email === 'admin@example.com') && password === 'duggy') {
        sessionStorage.setItem('userEmail', 'admin');
        sessionStorage.setItem('userRole', 'admin');
        navigate('/admin-dashboard');
        return;
      }

      // Get user from database
      const user = await database.getUserByEmail(email);
      
      if (!user) {
        setError('Invalid email or password');
        return;
      }

      if (user.password !== password) {
        setError('Invalid email or password');
        return;
      }

      // Only check pending status for teachers
      if (!user.approved && user.role === 'teacher') {
        setError('Your teacher account is pending admin approval');
        return;
      }

      // Get school info
      const school = user.school_id ? await database.getSchoolById(user.school_id) : null;

      // Store user info in session storage
      sessionStorage.setItem('userEmail', user.email);
      sessionStorage.setItem('userRole', user.role);
      sessionStorage.setItem('userName', user.name);
      if (school) {
        sessionStorage.setItem('userSchool', school.name);
      }

      // Navigate based on role
      if (user.role === 'teacher') {
        navigate('/teacher-dashboard');
      } else {
        navigate('/student-dashboard');
      }
    } catch (error) {
      console.error('Login error:', error);
      setError('An error occurred during login');
    }
  };

  return (
    <div className="login-container">
      <h2>Login</h2>
      
      <div className="storage-indicator">
        <div className="storage-dot mysql-active"></div>
        <span>Using MySQL Database</span>
      </div>

      {error && <div className="error-message">{error}</div>}

      <form onSubmit={handleLogin}>
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="text"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            style={{ color: '#ffffff' }}
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            style={{ color: '#ffffff' }}
          />
        </div>
        <div className="form-group">
          <label htmlFor="role">Role</label>
          <select
            id="role"
            value={role}
            onChange={(e) => setRole(e.target.value)}
            required
            style={{ color: '#ffffff' }}
          >
            <option value="student">Student</option>
            <option value="teacher">Teacher</option>
          </select>
        </div>
        <button type="submit" className="login-button">
          Login
        </button>
      </form>

      <p className="create-account-link">
        Don't have an account? <a href="/create-account">Create Account</a>
      </p>
    </div>
  );
}

export default Login;
