import React, { useState, useRef, useEffect } from 'react';
import './ChatBot.css';
import logo from './assets/logo.png';
import LatexFormatter from './LatexFormatter';

function ChatBot() {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [inputText, setInputText] = useState('');
  const [isListening, setIsListening] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const chatBoxRef = useRef(null);
  const recognitionRef = useRef(null);
  const audioContextRef = useRef(null);
  const userAnalyserRef = useRef(null);
  const aiAnalyserRef = useRef(null);

  useEffect(() => {
    // Initialize speech recognition
    const SpeechRecognitionAPI = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (SpeechRecognitionAPI) {
      recognitionRef.current = new SpeechRecognitionAPI();
      recognitionRef.current.continuous = false;
      recognitionRef.current.interimResults = false;
      recognitionRef.current.lang = 'en-US';

      recognitionRef.current.onresult = (event) => {
        const text = event.results[0][0].transcript;
        setInputText(text);
        sendMessage(text);
      };

      recognitionRef.current.onend = () => {
        setIsListening(false);
      };
    }

    // Initialize audio context
    try {
      audioContextRef.current = new (window.AudioContext || window.webkitAudioContext)();
      userAnalyserRef.current = audioContextRef.current.createAnalyser();
      userAnalyserRef.current.fftSize = 256;
      userAnalyserRef.current.smoothingTimeConstant = 0.8;

      aiAnalyserRef.current = audioContextRef.current.createAnalyser();
      aiAnalyserRef.current.fftSize = 256;
      aiAnalyserRef.current.smoothingTimeConstant = 0.8;
    } catch (e) {
      console.error("Error initializing audio context:", e);
    }

    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.stop();
      }
    };
  }, []);

  const toggleChat = () => {
    setIsOpen(!isOpen);
  };

  const startListening = () => {
    if (recognitionRef.current && !isListening) {
      recognitionRef.current.start();
      setIsListening(true);
    }
  };

  const stopListening = () => {
    if (recognitionRef.current && isListening) {
      recognitionRef.current.stop();
      setIsListening(false);
    }
  };

  const sendMessage = async (text = null) => {
    const messageToSend = text || inputText;
    if (!messageToSend.trim()) return;

    setIsLoading(true);

    // Add user message
    const userMessage = { type: 'user', text: messageToSend };
    setMessages(prev => [...prev, userMessage]);
    setInputText('');

    try {
      // Prepare conversation history
      const history = messages.map(msg => ({
        role: msg.type === 'user' ? 'user' : 'assistant',
        content: msg.text
      }));

      const response = await fetch('http://localhost:5000/chat', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ 
          message: messageToSend,
          history
        }),
      });

      if (!response.ok) throw new Error('Network response was not ok');

      const data = await response.json();

      // Add bot message
      const botMessage = { type: 'bot', text: data.response };
      setMessages(prev => [...prev, botMessage]);

      // Speak the response
      speakText(data.response);
    } catch (error) {
      console.error('Error:', error);
      setMessages(prev => [...prev, { type: 'error', text: 'Sorry, I encountered an error. Please try again.' }]);
    } finally {
      setIsLoading(false);
    }
  };

  const speakText = async (text) => {
    try {
      const response = await fetch('http://localhost:5000/tts', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ text }),
      });

      if (!response.ok) throw new Error('TTS request failed');

      const audioBlob = await response.blob();
      const audioUrl = URL.createObjectURL(audioBlob);
      const audio = new Audio(audioUrl);

      audio.onplay = () => {
        if (aiAnalyserRef.current) {
          animateBubble(chatBoxRef.current.lastChild, aiAnalyserRef.current, false);
        }
      };

      audio.onended = () => {
        if (aiAnalyserRef.current) {
          stopBubbleAnimation(chatBoxRef.current.lastChild);
        }
        URL.revokeObjectURL(audioUrl);
      };

      audio.play();
    } catch (error) {
      console.error('TTS error:', error);
    }
  };

  const animateBubble = (element, analyser, isUser) => {
    if (!element || !analyser) return null;

    const bufferLength = analyser.frequencyBinCount;
    const dataArray = new Uint8Array(bufferLength);

    function animate() {
      analyser.getByteFrequencyData(dataArray);
      let sum = 0;
      for (let i = 0; i < bufferLength; i++) {
        sum += dataArray[i];
      }
      const volume = sum / bufferLength;
      const scale = 1 + (volume / 128) * 0.5;
      element.style.transform = `scale(${scale})`;
      requestAnimationFrame(animate);
    }

    return requestAnimationFrame(animate);
  };

  const stopBubbleAnimation = (element) => {
    if (element) {
      element.style.transform = 'scale(1)';
    }
  };

  // Helper to split and render all LaTeX expressions in a message, and also render markdown headings
  const renderWithLatexAndMarkdown = (text) => {
    if (!text) return null;
    // Split by newlines to handle markdown headings and paragraphs
    const lines = text.split(/\n|\r\n?/);
    return lines.map((line, idx) => {
      // Markdown headings
      if (/^###\s+/.test(line)) {
        return <h3 key={idx} style={{marginTop: '1em'}}>{line.replace(/^###\s+/, '')}</h3>;
      } else if (/^##\s+/.test(line)) {
        return <h2 key={idx} style={{marginTop: '1em'}}>{line.replace(/^##\s+/, '')}</h2>;
      } else if (/^#\s+/.test(line)) {
        return <h1 key={idx} style={{marginTop: '1em'}}>{line.replace(/^#\s+/, '')}</h1>;
      } else if (line.trim() === '') {
        return <br key={idx} />;
      } else {
        // Render LaTeX within the line
        // Regex for $...$ (inline), \[...\] (block), \(...\) (inline), $$...$$ (block)
        const regex = /(\$\$[^$]+\$\$|\$[^$]+\$|\\\[[\s\S]*?\\\]|\\\([\s\S]*?\\\))/g;
        const parts = line.split(regex);
        return (
          <span key={idx} style={{display: 'block', marginTop: '0.25em'}}>
            {parts.map((part, i) => {
              if (part.match(regex)) {
                let latex = part;
                let displayMode = true;
                if (latex.startsWith('$$') && latex.endsWith('$$')) {
                  latex = latex.slice(2, -2);
                  displayMode = true;
                } else if (latex.startsWith('$') && latex.endsWith('$')) {
                  latex = latex.slice(1, -1);
                  displayMode = false;
                } else if (latex.startsWith('\\[') && latex.endsWith('\\]')) {
                  latex = latex.slice(2, -2);
                  displayMode = true;
                } else if (latex.startsWith('\\(') && latex.endsWith('\\)')) {
                  latex = latex.slice(2, -2);
                  displayMode = false;
                }
                return <LatexFormatter key={i} content={latex.trim()} displayMode={displayMode} />;
              } else {
                return <span key={i}>{part}</span>;
              }
            })}
          </span>
        );
      }
    });
  };

  return (
    <div className={`chatbot-container ${isOpen ? 'open' : ''}`}>
      <button className="chatbot-toggle" onClick={toggleChat}>
        <img src={logo} alt="Chat" />
      </button>

      <div className="chatbot-window">
        <div className="chatbot-header">
          <h3>AI Tutor</h3>
          <button className="close-button" onClick={toggleChat}>Ã—</button>
        </div>

        <div className="chatbot-messages" ref={chatBoxRef}>
          {messages.map((msg, index) => (
            <div key={index} className={`message ${msg.type}`}>
              {renderWithLatexAndMarkdown(msg.text)}
            </div>
          ))}
          {isLoading && (
            <div className="message bot">
              <div className="typing-indicator">
                <span></span>
                <span></span>
                <span></span>
              </div>
            </div>
          )}
        </div>

        <div className="chatbot-input">
          <textarea
            value={inputText}
            onChange={(e) => setInputText(e.target.value)}
            onKeyPress={(e) => {
              if (e.key === 'Enter' && !e.shiftKey) {
                e.preventDefault();
                sendMessage();
              }
            }}
            placeholder="Type your message..."
            disabled={isLoading}
          />
          <div className="chatbot-buttons">
            <button
              className={`mic-button ${isListening ? 'listening' : ''}`}
              onClick={isListening ? stopListening : startListening}
              disabled={isLoading}
            >
              ðŸŽ¤
            </button>
            <button onClick={() => sendMessage()} disabled={isLoading}>
              Send
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default ChatBot;
