import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import database from '../services/Database.js';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');

    try {
      // Admin login check
      if ((email === 'admin' || email === 'admin@example.com') && password === 'duggy') {
        console.log('Admin login');
        sessionStorage.setItem('userEmail', 'admin');
        sessionStorage.setItem('userRole', 'admin');
        navigate('/admin-dashboard');
        return;
      }

      // Use backend login
      const user = await database.login(email, password);

      if (!user) {
        setError('Invalid email or password');
        return;
      }

      // Only check pending status for teachers
      if (!user.approved && user.role === 'teacher') {
        setError('Your teacher account is pending admin approval');
        return;
      }

      // Store user info in session storage
      sessionStorage.setItem('userEmail', user.email);
      sessionStorage.setItem('userRole', user.role);
      sessionStorage.setItem('userName', user.name);
      if (user.school) {
        sessionStorage.setItem('userSchool', user.school);
      }

      // Navigate based on role
      if (user.role === 'teacher') {
        navigate('/teacher-dashboard');
      } else {
        navigate('/student-dashboard');
      }
    } catch (error) {
      console.error('Login error:', error);
      setError('Invalid email or password');
    }
  };

  return (
    <div className="login-container">
      <div className="header-with-back">
        <h2>Login</h2>
      </div>
      <form onSubmit={handleLogin}>
        {error && <div className="error-message">{error}</div>}
        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Login</button>
        <div className="create-account">
          <p>Don't have an account? <button onClick={() => navigate('/register')} type="button">Create Account</button></p>
        </div>
      </form>
    </div>
  );
}

export default Login;
