import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import BackButton from './BackButton';
import wasabiStorage from '../services/WasabiStorage';

function AdminDashboard() {
  const [activeTab, setActiveTab] = useState('schools');
  const [schools, setSchools] = useState([]);
  const [teachers, setTeachers] = useState([]);
  const [approvedTeachers, setApprovedTeachers] = useState({});
  const [selectedSchool, setSelectedSchool] = useState(null);
  const [newSchoolName, setNewSchoolName] = useState('');
  const [error, setError] = useState('');
  const [showCreateSchool, setShowCreateSchool] = useState(false);
  const navigate = useNavigate();

  // Separate loading functions for faster initial load
  const loadPendingTeachers = async () => {
    try {
      const teachersData = await wasabiStorage.listObjects('teacher-approval/');
      const pendingTeachers = [];

      const teacherPromises = teachersData.map(async (teacher) => {
        if (teacher.Key.endsWith('.json')) {
          const email = teacher.Key.replace('teacher-approval/', '').replace('.json', '');
          const teacherData = await wasabiStorage.getData(`teacher-approval/${email}.json`);
          if (teacherData) {
            return { ...teacherData, email };
          }
        }
        return null;
      });

      const resolvedTeachers = await Promise.all(teacherPromises);
      setTeachers(resolvedTeachers.filter(t => t !== null));
    } catch (error) {
      console.error('Error loading pending teachers:', error);
    }
  };

  const loadSchoolsAndTeachers = async () => {
    try {
      // Load schools first
      const schoolsData = await wasabiStorage.getData('schools.json') || [];
      setSchools(schoolsData);

      // Then load approved teachers for each school
      const approvedTeachersMap = {};
      for (const school of schoolsData) {
        const teachersList = await wasabiStorage.listObjects(`${school.name}/teachers/`);
        approvedTeachersMap[school.name] = [];

        for (const teacher of teachersList) {
          if (teacher.Key.endsWith('/info.json')) {
            const email = teacher.Key.split('/')[2];
            const teacherData = await wasabiStorage.getData(`${school.name}/teachers/${email}/info.json`);
            if (teacherData && teacherData.approved) {
              approvedTeachersMap[school.name].push({
                ...teacherData,
                email
              });
            }
          }
        }
      }
      setApprovedTeachers(approvedTeachersMap);
    } catch (error) {
      console.error('Error loading schools and teachers:', error);
      setError('Failed to load data');
    }
  };

  useEffect(() => {
    const init = async () => {
      const isConnected = await wasabiStorage.testConnection();
      if (!isConnected) {
        setError('Failed to connect to storage');
        return;
      }
      // Load pending teachers immediately
      loadPendingTeachers();
      // Load schools and approved teachers
      loadSchoolsAndTeachers();
    };
    init();
  }, []);

  const handleCreateSchool = async () => {
    if (!newSchoolName.trim()) {
      setError('Please enter a school name');
      return;
    }

    try {
      const newSchool = {
        id: Date.now(),
        name: newSchoolName.trim(),
        createdAt: new Date().toISOString()
      };

      // Create school folder structure
      const schoolFolderKey = `${newSchoolName.trim()}/`;
      const teachersFolderKey = `${newSchoolName.trim()}/teachers/`;
      const studentsFolderKey = `${newSchoolName.trim()}/students/`;

      await Promise.all([
        wasabiStorage.s3.putObject({
          Bucket: wasabiStorage.bucket,
          Key: schoolFolderKey,
          Body: '',
          ContentType: 'application/x-directory'
        }).promise(),
        wasabiStorage.s3.putObject({
          Bucket: wasabiStorage.bucket,
          Key: teachersFolderKey,
          Body: '',
          ContentType: 'application/x-directory'
        }).promise(),
        wasabiStorage.s3.putObject({
          Bucket: wasabiStorage.bucket,
          Key: studentsFolderKey,
          Body: '',
          ContentType: 'application/x-directory'
        }).promise()
      ]);

      await wasabiStorage.saveData(`${newSchoolName.trim()}/info.json`, newSchool);
      const updatedSchools = [...schools, newSchool];
      await wasabiStorage.saveData('schools.json', updatedSchools);

      setSchools(updatedSchools);
      setNewSchoolName('');
      setShowCreateSchool(false);
    } catch (error) {
      console.error('Error creating school:', error);
      setError('Failed to create school');
    }
  };

  const handleApproveTeacher = async (email) => {
    try {
      const teacherData = await wasabiStorage.getData(`teacher-approval/${email}.json`);
      if (!teacherData) {
        setError('Teacher data not found');
        return;
      }

      const schoolName = teacherData.school;
      const teacherFolderKey = `${schoolName}/teachers/${email}/`;
      const classesFolderKey = `${schoolName}/teachers/${email}/classes/`;

      await Promise.all([
        wasabiStorage.s3.putObject({
          Bucket: wasabiStorage.bucket,
          Key: teacherFolderKey,
          Body: '',
          ContentType: 'application/x-directory'
        }).promise(),
        wasabiStorage.s3.putObject({
          Bucket: wasabiStorage.bucket,
          Key: classesFolderKey,
          Body: '',
          ContentType: 'application/x-directory'
        }).promise()
      ]);

      const approvedTeacherData = {
        ...teacherData,
        approved: true,
        approvedAt: new Date().toISOString()
      };

      await wasabiStorage.saveData(`${schoolName}/teachers/${email}/info.json`, approvedTeacherData);
      await wasabiStorage.deleteData(`teacher-approval/${email}.json`);

      setTeachers(prev => prev.filter(t => t.email !== email));
      setApprovedTeachers(prev => ({
        ...prev,
        [schoolName]: [...(prev[schoolName] || []), approvedTeacherData]
      }));
    } catch (error) {
      console.error('Error approving teacher:', error);
      setError('Failed to approve teacher');
    }
  };

  const handleDenyTeacher = async (email) => {
    try {
      await wasabiStorage.deleteData(`teacher-approval/${email}.json`);
      setTeachers(prev => prev.filter(t => t.email !== email));
    } catch (error) {
      console.error('Error denying teacher:', error);
      setError('Failed to deny teacher');
    }
  };

  return (
    <div className="admin-dashboard" style={{ 
      padding: '20px',
      maxWidth: '1200px',
      margin: '0 auto',
      minHeight: '100vh',
      backgroundColor: '#1a1a1a',
      color: 'white'
    }}>
      <BackButton destination="/" />
      <h2 style={{ fontSize: '2.5rem', marginBottom: '2rem' }}>Admin Dashboard</h2>

      {error && <div style={{ 
        backgroundColor: '#dc3545', 
        color: 'white', 
        padding: '12px', 
        borderRadius: '6px', 
        marginBottom: '1rem' 
      }}>{error}</div>}

      <div className="schools-section" style={{ marginBottom: '2rem' }}>
        <h3 style={{ fontSize: '1.8rem', marginBottom: '1rem' }}>Schools</h3>

        {showCreateSchool ? (
          <div style={{ 
            backgroundColor: '#2d2d2d',
            padding: '1rem',
            borderRadius: '8px',
            marginBottom: '1rem'
          }}>
            <input
              type="text"
              value={newSchoolName}
              onChange={(e) => setNewSchoolName(e.target.value)}
              placeholder="Enter school name"
              style={{
                width: '100%',
                padding: '0.5rem',
                marginBottom: '1rem',
                backgroundColor: '#1a1a1a',
                border: '1px solid #444',
                borderRadius: '4px',
                color: 'white'
              }}
            />
            <div style={{ display: 'flex', gap: '0.5rem' }}>
              <button 
                onClick={handleCreateSchool}
                style={{
                  padding: '0.5rem 1rem',
                  backgroundColor: '#007bff',
                  border: 'none',
                  borderRadius: '4px',
                  color: 'white',
                  cursor: 'pointer'
                }}
              >
                Create School
              </button>
              <button 
                onClick={() => setShowCreateSchool(false)}
                style={{
                  padding: '0.5rem 1rem',
                  backgroundColor: '#6c757d',
                  border: 'none',
                  borderRadius: '4px',
                  color: 'white',
                  cursor: 'pointer'
                }}
              >
                Cancel
              </button>
            </div>
          </div>
        ) : (
          <button 
            onClick={() => setShowCreateSchool(true)}
            style={{
              padding: '0.5rem 1rem',
              backgroundColor: '#007bff',
              border: 'none',
              borderRadius: '4px',
              color: 'white',
              cursor: 'pointer',
              marginBottom: '1rem'
            }}
          >
            Create New School
          </button>
        )}

        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
          gap: '1rem'
        }}>
          {schools.map(school => (
            <div 
              key={school.id}
              style={{
                backgroundColor: selectedSchool === school.name ? '#3d3d3d' : '#2d2d2d',
                padding: '1rem',
                borderRadius: '8px',
                cursor: 'pointer'
              }}
              onClick={() => setSelectedSchool(selectedSchool === school.name ? null : school.name)}
            >
              <div style={{ 
                display: 'flex', 
                justifyContent: 'space-between', 
                alignItems: 'center',
                marginBottom: '0.5rem'
              }}>
                <h4 style={{ margin: 0 }}>{school.name}</h4>
                <span style={{ 
                  backgroundColor: '#007bff',
                  padding: '0.25rem 0.5rem',
                  borderRadius: '4px',
                  fontSize: '0.8rem'
                }}>
                  {approvedTeachers[school.name]?.length || 0} Teachers
                </span>
              </div>

              {selectedSchool === school.name && approvedTeachers[school.name]?.length > 0 && (
                <div style={{ marginTop: '1rem' }}>
                  {approvedTeachers[school.name].map(teacher => (
                    <div 
                      key={teacher.email}
                      style={{
                        backgroundColor: '#1a1a1a',
                        padding: '0.5rem',
                        borderRadius: '4px',
                        marginBottom: '0.5rem'
                      }}
                    >
                      <div>{teacher.name}</div>
                      <div style={{ fontSize: '0.8rem', color: '#888' }}>{teacher.email}</div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          ))}
        </div>
      </div>

      {teachers.length > 0 && (
        <div className="teachers-section">
          <h3 style={{ fontSize: '1.8rem', marginBottom: '1rem' }}>Pending Teacher Approvals</h3>
          <div style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
            {teachers.map(teacher => (
              <div 
                key={teacher.email}
                style={{
                  backgroundColor: '#2d2d2d',
                  padding: '1rem',
                  borderRadius: '8px',
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'center'
                }}
              >
                <div>
                  <div style={{ fontSize: '1.1rem' }}>{teacher.name}</div>
                  <div style={{ color: '#888' }}>{teacher.email}</div>
                  <div style={{ color: '#888', fontSize: '0.9rem' }}>School: {teacher.school}</div>
                </div>
                <div style={{ display: 'flex', gap: '0.5rem' }}>
                  <button
                    onClick={() => handleApproveTeacher(teacher.email)}
                    style={{
                      padding: '0.5rem 1rem',
                      backgroundColor: '#28a745',
                      border: 'none',
                      borderRadius: '4px',
                      color: 'white',
                      cursor: 'pointer'
                    }}
                  >
                    Approve
                  </button>
                  <button
                    onClick={() => handleDenyTeacher(teacher.email)}
                    style={{
                      padding: '0.5rem 1rem',
                      backgroundColor: '#dc3545',
                      border: 'none',
                      borderRadius: '4px',
                      color: 'white',
                      cursor: 'pointer'
                    }}
                  >
                    Deny
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

export default AdminDashboard;
