import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import database from '../services/Database.js';

function CreateAccount() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [role, setRole] = useState('student');
  const [school, setSchool] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      // Form validation
      if (!name || !email || !password || !confirmPassword || !school) {
        setError('All fields are required');
        return;
      }

      if (password !== confirmPassword) {
        setError('Passwords do not match');
        return;
      }

      // Email validation
      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      if (!emailRegex.test(email)) {
        setError('Please enter a valid email address');
        return;
      }

      // Password validation (at least 6 characters)
      if (password.length < 6) {
        setError('Password must be at least 6 characters long');
        return;
      }

      // Check if user already exists
      const existingUser = await database.getUserByEmail(email);
      if (existingUser) {
        setError('An account with this email already exists');
        return;
      }

      // Get or create school
      let schoolRecord = await database.getSchoolByName(school);
      if (!schoolRecord) {
        // Create new school with current timestamp as ID
        schoolRecord = await database.createSchool({
          id: Date.now(),
          name: school
        });
      }

      // Create new user
      await database.createUser({
        email,
        name,
        password,
        role,
        school_id: schoolRecord.id,
        approved: role === 'student' // Students are auto-approved
      });

      // Show different message based on role
      if (role === 'teacher') {
        alert('Your teacher account has been created and is pending approval by an administrator.');
      } else {
        alert('Your account has been created successfully!');
      }

      // Navigate back to login page
      navigate('/login');
    } catch (error) {
      console.error('Registration error:', error);
      setError(`Registration failed: ${error.message}`);
    }
  };

  return (
    <div className="login-container">
      <div className="header-with-back">
        <button onClick={() => navigate('/login')} className="back-button">
          ← Back
        </button>
        <h2>Create Account</h2>
        <div className="spacer"></div>
      </div>

      <div className="storage-indicator">
        <div className="storage-dot mysql-active"></div>
        <span>Using MySQL Database</span>
      </div>

      {error && <div className="error-message">{error}</div>}

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="name">Full Name</label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="school">School</label>
          <input
            type="text"
            id="school"
            value={school}
            onChange={(e) => setSchool(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="confirmPassword">Confirm Password</label>
          <input
            type="password"
            id="confirmPassword"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="role">Account Type</label>
          <select
            id="role"
            value={role}
            onChange={(e) => setRole(e.target.value)}
          >
            <option value="student">Student</option>
            <option value="teacher">Teacher</option>
          </select>
        </div>

        <button type="submit" className="login-button">
          Create Account
        </button>
      </form>
    </div>
  );
}

export default CreateAccount;
