import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { wasabiStorage } from '../services/WasabiStorage';

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [role, setRole] = useState('student');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');

        try {
            // Check pending approval first
            const pendingPath = role === 'teacher' ? 
                `teacher-approval/${email}.json` : 
                `student-approval/${email}.json`;

            const pendingUser = await wasabiStorage.getData(pendingPath);
            if (pendingUser) {
                setError('Your account is pending approval');
                return;
            }

            // Check approved users
            const schools = await wasabiStorage.getData('schools.json') || {};
            let userData = null;

            for (const school of Object.values(schools)) {
                const userPath = role === 'teacher' ?
                    `${school.name}/teachers/${email}/info.json` :
                    `${school.name}/students/${email}/info.json`;

                const user = await wasabiStorage.getData(userPath);
                if (user) {
                    userData = user;
                    break;
                }
            }

            if (!userData) {
                setError('Invalid email or password');
                return;
            }

            if (userData.password !== password) {
                setError('Invalid email or password');
                return;
            }

            // Store user info in session storage
            sessionStorage.setItem('userEmail', email);
            sessionStorage.setItem('userRole', role);
            sessionStorage.setItem('userName', userData.name);
            sessionStorage.setItem('schoolName', userData.school);

            // Navigate based on role
            navigate(role === 'teacher' ? '/teacher-dashboard' : '/student-dashboard');
        } catch (error) {
            console.error('Login error:', error);
            setError('An error occurred during login');
        }
    };

    return (
        <div className="login-container">
            <h2>Login</h2>
            {error && <div className="error-message">{error}</div>}
            <form onSubmit={handleLogin}>
                <div className="form-group">
                    <label htmlFor="email">Email</label>
                    <input
                        type="email"
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="password">Password</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="role">Role</label>
                    <select
                        id="role"
                        value={role}
                        onChange={(e) => setRole(e.target.value)}
                        className="role-select"
                    >
                        <option value="student">Student</option>
                        <option value="teacher">Teacher</option>
                    </select>
                </div>
                <button type="submit" className="login-button">Login</button>
            </form>
            <p style={{ marginTop: '1rem', textAlign: 'center', color: 'var(--text-secondary)' }}>
                Don't have an account?{' '}
                <a href="/create-account" className="nav-link">Create Account</a>
            </p>
        </div>
    );
}

export default Login;
