import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import BackButton from './BackButton';
import wasabiStorage from '../services/WasabiStorage';

function CreateAccount() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [schools, setSchools] = useState([]);
  const [selectedSchool, setSelectedSchool] = useState('');
  const [isTeacher, setIsTeacher] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    loadSchools();
  }, []);

  const loadSchools = async () => {
    try {
      const schoolsData = await wasabiStorage.getData('schools.json') || [];
      setSchools(schoolsData);
      if (schoolsData.length > 0) {
        setSelectedSchool(schoolsData[0].name);
      }
    } catch (error) {
      console.error('Error loading schools:', error);
      setError('Failed to load schools');
    }
  };

  const handleCreateAccount = async (e) => {
    e.preventDefault();
    setError('');

    try {
      // Connect to Wasabi
      const isConnected = await wasabiStorage.testConnection();
      if (!isConnected) {
        setError('Failed to connect to storage');
        return;
      }

      // For teachers, check if they selected a school
      if (isTeacher && !selectedSchool) {
        setError('Please select a school');
        return;
      }

      // Check if user already exists
      const userPath = isTeacher 
        ? `${selectedSchool}/teachers/${email}/info.json`
        : `${selectedSchool}/students/${email}/info.json`;
      const pendingPath = isTeacher 
        ? `${selectedSchool}/teachers/${email}/pending.json`
        : null;

      const existingUser = await wasabiStorage.getData(userPath);
      const pendingUser = pendingPath ? await wasabiStorage.getData(pendingPath) : null;

      if (existingUser || pendingUser) {
        setError('An account with this email already exists');
        return;
      }

      // Create user data
      const userData = {
        name,
        email,
        password,
        createdAt: new Date().toISOString()
      };

      // Save user data
      if (isTeacher) {
        // Save teacher as pending
        await wasabiStorage.putData(`${selectedSchool}/teachers/${email}/pending.json`, userData);
        setError('Your teacher account has been created and is pending admin approval');
      } else {
        // Save student directly
        await wasabiStorage.putData(`${selectedSchool}/students/${email}/info.json`, userData);
        // Store user info in session storage
        sessionStorage.setItem('userEmail', email);
        sessionStorage.setItem('userRole', 'student');
        sessionStorage.setItem('userName', name);
        sessionStorage.setItem('userSchool', selectedSchool);
        navigate('/student-dashboard');
      }
    } catch (error) {
      console.error('Create account error:', error);
      setError('An error occurred while creating your account');
    }
  };

  return (
    <div className="create-account-container">
      <BackButton destination="/" />
      <h2>Create Account</h2>

      <div className="storage-indicator">
        <div className="storage-dot wasabi-active"></div>
        <span>Using Wasabi Cloud Storage</span>
      </div>

      {error && <div className="error-message">{error}</div>}

      <form onSubmit={handleCreateAccount}>
        <div className="form-group">
          <label htmlFor="name">Name</label>
          <input
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
            placeholder="Enter your name"
            style={{ color: '#000' }}
          />
        </div>

        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            placeholder="Enter your email"
            style={{ color: '#000' }}
          />
        </div>

        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            placeholder="Enter your password"
            style={{ color: '#000' }}
          />
        </div>

        <div className="form-group">
          <label>
            <input
              type="checkbox"
              checked={isTeacher}
              onChange={(e) => setIsTeacher(e.target.checked)}
            /> I am a teacher
          </label>
        </div>

        {isTeacher && (
          <div className="form-group">
            <label htmlFor="school">Select School</label>
            <select
              id="school"
              value={selectedSchool}
              onChange={(e) => setSelectedSchool(e.target.value)}
              required
              style={{ color: '#000' }}
            >
              {schools.map(school => (
                <option key={school.id} value={school.name}>
                  {school.name}
                </option>
              ))}
            </select>
          </div>
        )}

        <button type="submit">Create Account</button>
      </form>

      <p>
        Already have an account?{' '}
        <button 
          className="secondary-button"
          onClick={() => navigate('/')}
        >
          Login
        </button>
      </p>
    </div>
  );
}

export default CreateAccount;
