import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import BackButton from './BackButton';
import wasabiStorage from '../services/WasabiStorage';

function CreateAccount() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [schools, setSchools] = useState([]);
  const [selectedSchool, setSelectedSchool] = useState('');
  const [role, setRole] = useState('student');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    loadSchools();
  }, []);

  const loadSchools = async () => {
    try {
      const schoolsData = await wasabiStorage.getData('schools.json') || [];
      setSchools(schoolsData);
      if (schoolsData.length > 0) {
        setSelectedSchool(schoolsData[0].name);
      }
    } catch (error) {
      console.error('Error loading schools:', error);
      setError('Failed to load schools');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setSuccess(null);

    try {
      const result = await registerTeacher({
        name,
        email,
        password,
        school: selectedSchool
      });

      if (result.success) {
        setSuccess('Your registration has been submitted for approval. You will receive an email when your account is approved.');
        setName('');
        setEmail('');
        setPassword('');
        setSelectedSchool('');
      } else {
        setError(result.error || 'Registration failed');
      }
    } catch (err) {
      setError('An error occurred during registration');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="create-account-container">
      <BackButton destination="/" />
      <h2>Create Account</h2>

      <div className="storage-indicator">
        <div className="storage-dot wasabi-active"></div>
        <span>Using Wasabi Cloud Storage</span>
      </div>

      {error && (
        <div className="error-box">
          {error}
        </div>
      )}
      {success && (
        <div className="success-box">
          <h3>Registration Submitted</h3>
          <p>{success}</p>
          <button 
            className="back-to-login"
            onClick={() => navigate('/login')}
          >
            Back to Login
          </button>
        </div>
      )}
      {!success && (
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              style={{ color: '#ffffff' }}
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              style={{ color: '#ffffff' }}
            />
          </div>
          <div className="form-group">
            <label htmlFor="name">Full Name</label>
            <input
              type="text"
              id="name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
              style={{ color: '#ffffff' }}
            />
          </div>
          <div className="form-group">
            <label htmlFor="role">Role</label>
            <select
              id="role"
              value={role}
              onChange={(e) => setRole(e.target.value)}
              required
              style={{ color: '#ffffff' }}
            >
              <option value="student">Student</option>
              <option value="teacher">Teacher</option>
            </select>
          </div>
          {role === 'teacher' && (
            <div className="form-group">
              <label htmlFor="school">School</label>
              <select
                id="school"
                value={selectedSchool}
                onChange={(e) => setSelectedSchool(e.target.value)}
                required
                style={{ color: '#ffffff' }}
              >
                <option value="">Select a school</option>
                {schools.map((school) => (
                  <option key={school.id} value={school.name}>
                    {school.name}
                  </option>
                ))}
              </select>
            </div>
          )}
          <button type="submit" className="create-account-button">
            Create Account
          </button>
        </form>
      )}

      <p className="login-link">
        Already have an account?{' '}
        <button 
          className="secondary-button"
          onClick={() => navigate('/')}
        >
          Login
        </button>
      </p>
    </div>
  );
}

export default CreateAccount;

const styles = `
    .create-account-container {
        max-width: 500px;
        margin: 0 auto;
        padding: 20px;
    }

    .error-box {
        background-color: #ffebee;
        color: #c62828;
        padding: 15px;
        border-radius: 4px;
        margin-bottom: 20px;
        border: 1px solid #ffcdd2;
    }

    .success-box {
        background-color: #e8f5e9;
        color: #2e7d32;
        padding: 20px;
        border-radius: 4px;
        margin-bottom: 20px;
        border: 1px solid #c8e6c9;
        text-align: center;
    }

    .success-box h3 {
        margin-top: 0;
        color: #1b5e20;
    }

    .back-to-login {
        background-color: #4caf50;
        color: white;
        border: none;
        padding: 10px 20px;
        border-radius: 4px;
        cursor: pointer;
        margin-top: 15px;
        font-size: 16px;
    }

    .back-to-login:hover {
        background-color: #388e3c;
    }
`;
