import React from 'react';
import { Link } from 'react-router-dom';

function LandingPage() {
  return (
    <div style={{
      maxWidth: '1200px',
      margin: '0 auto',
      padding: '24px',
      minHeight: '100vh',
      backgroundColor: '#121212'
    }}>
      <div style={{
        textAlign: 'center',
        padding: '48px 0'
      }}>
        <h1 style={{ 
          margin: 0,
          fontSize: '48px',
          color: '#fff',
          marginBottom: '24px'
        }}>Welcome to EduBlock</h1>
        <p style={{
          fontSize: '24px',
          color: '#888',
          marginBottom: '48px'
        }}>Secure Education Platform using Blockchain Storage</p>
        <div style={{
          display: 'flex',
          justifyContent: 'center',
          gap: '24px'
        }}>
          <Link to="/login" style={{
            padding: '12px 24px',
            fontSize: '18px',
            backgroundColor: '#007bff',
            color: '#fff',
            textDecoration: 'none',
            borderRadius: '8px',
            transition: 'background-color 0.2s'
          }}>Login</Link>
          <Link to="/create-account" style={{
            padding: '12px 24px',
            fontSize: '18px',
            backgroundColor: '#dc3545',
            color: '#fff',
            textDecoration: 'none',
            borderRadius: '8px',
            transition: 'background-color 0.2s'
          }}>Create Account</Link>
        </div>
      </div>

      <div style={{
        backgroundColor: '#1a1a1a',
        borderRadius: '16px',
        padding: '32px',
        marginTop: '48px'
      }}>
        <h2 style={{ 
          fontSize: '32px',
          color: '#fff',
          marginBottom: '24px'
        }}>Features</h2>
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
          gap: '24px'
        }}>
          <div style={{
            backgroundColor: '#1a1a1a',
            padding: '24px',
            borderRadius: '16px'
          }}>
            <h3 style={{ 
              fontSize: '24px',
              color: '#fff',
              marginBottom: '12px'
            }}>Secure Storage</h3>
            <p style={{ color: '#888' }}>Powered by Wasabi and Infura blockchain storage solutions</p>
          </div>
          <div style={{
            backgroundColor: '#1a1a1a',
            padding: '24px',
            borderRadius: '16px'
          }}>
            <h3 style={{ 
              fontSize: '24px',
              color: '#fff',
              marginBottom: '12px'
            }}>Role-based Access</h3>
            <p style={{ color: '#888' }}>Separate dashboards for students, teachers, and administrators</p>
          </div>
          <div style={{
            backgroundColor: '#1a1a1a',
            padding: '24px',
            borderRadius: '16px'
          }}>
            <h3 style={{ 
              fontSize: '24px',
              color: '#fff',
              marginBottom: '12px'
            }}>Decentralized</h3>
            <p style={{ color: '#888' }}>Your data is stored securely across decentralized networks</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default LandingPage;
